import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000'); // Cambia esto si usas un túnel

const VideoChat = () => {
    const [remoteStream, setRemoteStream] = useState(null);
    const [recipientId, setRecipientId] = useState(null); // Agregar estado para recipientId
    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);
    const peerConnection = useRef(new RTCPeerConnection({
        iceServers: [{ urls: 'stun:stun.l.google.com:19302' }]
    }));

    useEffect(() => {
        // Manejar la conexión de video local
        navigator.mediaDevices.getUserMedia({ video: true, audio: true })
            .then(stream => {
                localVideoRef.current.srcObject = stream;
                stream.getTracks().forEach(track => {
                    peerConnection.current.addTrack(track, stream);
                });
            });

        // Escuchar eventos de Socket.io
        socket.on('callReceived', (data) => {
            const { from } = data;
            setRecipientId(from); // Establecer el recipientId
            startCall(from);
        });

        socket.on('callOffer', async (data) => {
            const { offer, from } = data;
            await peerConnection.current.setRemoteDescription(new RTCSessionDescription(offer));
            const answer = await peerConnection.current.createAnswer();
            await peerConnection.current.setLocalDescription(answer);
            socket.emit('callAnswer', { answer, to: from });
        });

        socket.on('callAnswer', async (data) => {
            const { answer } = data;
            await peerConnection.current.setRemoteDescription(new RTCSessionDescription(answer));
        });

        peerConnection.current.ontrack = (event) => {
            setRemoteStream(event.streams[0]);
            remoteVideoRef.current.srcObject = event.streams[0];
        };

        // Manejar ICE candidates
        peerConnection.current.onicecandidate = (event) => {
            if (event.candidate) {
                socket.emit('iceCandidate', { candidate: event.candidate, to: recipientId }); // Usar recipientId
            }
        };

        return () => {
            socket.off('callReceived');
            socket.off('callOffer');
            socket.off('callAnswer');
        };
    }, [recipientId]); // Agregar recipientId a las dependencias

    const startCall = async (id) => {
        const offer = await peerConnection.current.createOffer();
        await peerConnection.current.setLocalDescription(offer);
        socket.emit('startCall', { recipientId: id });
        socket.emit('callOffer', { offer, to: id });
    };

    return (
        <div>
            <video ref={localVideoRef} autoPlay muted style={{ width: '300px' }} />
            <video ref={remoteVideoRef} autoPlay style={{ width: '300px' }} />
        </div>
    );
};

export default VideoChat;
