import React, { useEffect, useRef, useState } from 'react';
import * as faceapi from 'face-api.js';

const FaceRecognition = () => {
  const [image, setImage] = useState(null);
  const [detections, setDetections] = useState(null);
  const imageRef = useRef();

  useEffect(() => {
    const loadModels = async () => {
      // Cargar los modelos desde la carpeta /models
      await faceapi.nets.ssdMobilenetv1.loadFromUri('/models');
      await faceapi.nets.faceLandmark68Net.loadFromUri('/models');
      await faceapi.nets.faceRecognitionNet.loadFromUri('/models');

    };
    loadModels();
  }, []);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    setImage(URL.createObjectURL(file));

    const imgElement = imageRef.current;

    // Detectar caras cuando la imagen esté lista
    imgElement.onload = async () => {
      const detections = await faceapi.detectAllFaces(imgElement)
        .withFaceLandmarks()
        .withFaceDescriptors();

      setDetections(detections);
      console.log(detections); // Ver los resultados en la consola
    };
  };

  return (
    <div>
      <h1>Reconocimiento de Personas</h1>
      <input type="file" onChange={handleImageUpload} />
      {image && <img ref={imageRef} src={image} alt="Uploaded" style={{ width: '300px' }} />}
      {detections && (
        <div>
          <h2>Detecciones:</h2>
          <pre>{JSON.stringify(detections, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default FaceRecognition;
