import React, { useState } from 'react';
import axios from 'axios';

const FaceRecognition = () => {
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    setImage(URL.createObjectURL(file));

    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post('http://localhost:5000/recognize-face', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setMessage(response.data.message);
    } catch (error) {
      console.error('Error al reconocer la imagen', error);
    }
  };

  return (
    <div>
      <h1>Reconocimiento de Personas</h1>
      <input type="file" onChange={handleImageUpload} />
      {image && <img src={image} alt="Upload Preview" style={{ width: '300px' }} />}
      {message && <p>{message}</p>}
    </div>
  );
};

export default FaceRecognition;
