import React, { useEffect, useRef, useState } from 'react';
import Peer from 'simple-peer';
import * as faceapi from 'face-api.js';
import io from 'socket.io-client';

// Conexión al servidor WebSocket
const socket = io.connect('http://localhost:5000');

const VideoChat = () => {
  const [stream, setStream] = useState(null); // Flujo de video de la cámara web
  const [me, setMe] = useState(""); // ID del socket del usuario
  const [peer, setPeer] = useState(null); // Conexión WebRTC
  const myVideoRef = useRef(); // Referencia al video local
  const userVideoRef = useRef(); // Referencia al video remoto
  const [detections, setDetections] = useState(null); // Detecciones faciales

  // Configurar la cámara y cargar los modelos de face-api.js
  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      setStream(stream);
      if (myVideoRef.current) {
        myVideoRef.current.srcObject = stream;
      }
    });

    // Cargar los modelos de face-api.js
    const loadModels = async () => {
      await faceapi.nets.ssdMobilenetv1.loadFromUri('/models');
      await faceapi.nets.faceLandmark68Net.loadFromUri('/models');
      await faceapi.nets.faceRecognitionNet.loadFromUri('/models');
    };
    loadModels();

    // Obtener el ID del socket del usuario
    socket.on('me', (id) => {
      setMe(id);
    });
  }, []);

  // Detectar rostros en tiempo real en el video
  useEffect(() => {
    const interval = setInterval(async () => {
      if (myVideoRef.current && stream) {
        const detections = await faceapi.detectAllFaces(
          myVideoRef.current,
          new faceapi.TinyFaceDetectorOptions()
        ).withFaceLandmarks().withFaceDescriptors();
        setDetections(detections);
      }
    }, 100); // Cada 100 ms
    return () => clearInterval(interval);
  }, [stream]);

  // Iniciar la conexión con WebRTC
  const startPeer = (initiator) => {
    const newPeer = new Peer({ initiator, trickle: false, stream });

    newPeer.on('signal', (data) => {
      socket.emit('signal', { to: "partner-id", signal: data });
    });

    newPeer.on('stream', (userStream) => {
      if (userVideoRef.current) {
        userVideoRef.current.srcObject = userStream;
      }
    });

    socket.on('signal', (signalData) => {
      newPeer.signal(signalData.signal);
    });

    setPeer(newPeer);
  };

  return (
    <div>
      <h1>Video Chat con Reconocimiento Facial</h1>

      {/* Mi video */}
      <video ref={myVideoRef} autoPlay muted style={{ width: '300px' }} />

      {/* Video del usuario remoto */}
      <video ref={userVideoRef} autoPlay style={{ width: '300px' }} />

      {/* Botón para iniciar videollamada */}
      <button onClick={() => startPeer(true)}>Iniciar llamada</button>

      {/* Botón para unirse a la llamada */}
      <button onClick={() => startPeer(false)}>Unirse a llamada</button>

      {/* Mostrar detecciones faciales */}
      {detections && (
        <div>
          <h2>Detecciones:</h2>
          <pre>{JSON.stringify(detections, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default VideoChat;
